## holds quizzes

class Quiz:
    def __init__(self, question, answer, description):
        self.question = question
        self.answer = answer
        self.description = description

class smallQuiz:
    def __init__(self, q1, q2, q3, q4, q5, a1, a2, a3, a4, a5, headline, byline):
        self.questions = [q1, q2, q3, q4, q5]
        self.answers = [a1, a2, a3, a4, a5]
        self.headline = headline
        self.byline = byline

class footballQuiz:
    def __init__(self, p1, p2, p3, p4, p5, p6, p7, p8, p9, p10, p11, a1, a2, a3, a4, a5, a6, a7, a8, a9, a10, a11, headline, byline):
        self.questions = [p1, p2, p3, p4, p5, p6, p7, p8, p9, p10, p11]
        self.answers = [a1, a2, a3, a4, a5, a6, a7, a8, a9, a10, a11]
        self.headline = headline
        self.byline = byline 

class mediumQuiz:
    def __init__(self, p1, p2, p3, p4, p5, p6, p7, p8, p9, p10, a1, a2, a3, a4, a5, a6, a7, a8, a9, a10, headline, byline):
        self.questions = [p1, p2, p3, p4, p5, p6, p7, p8, p9, p10]
        self.answers = [a1, a2, a3, a4, a5, a6, a7, a8, a9, a10]
        self.headline = headline
        self.byline = byline 

class pictureQuiz:
    def __init__(self, url1, url2, url3, url4, url5, url6, a1, a2, a3, a4, a5, a6, headline, byline):
        self.questions = [url1, url2, url3, url4, url5, url6]
        self.answers = [a1, a2, a3, a4, a5, a6]
        self.headline = headline
        self.byline = byline

class pictureQuiz2:
    def __init__(self, url1, url2, url3, url4, url5, url6, a1, headline, byline):
        self.questions = [url1, url2, url3, url4, url5, url6]
        self.answers = a1
